'''
Between letters - one space
Between words - four spaces
'''

def code(d):
    text = input('Type your text:\n').upper()
    for i in range(len(text)):
        if text[i] == ' ':
            print(sep = '', end = '   ')
        for j in d.keys():
            if text[i] == j:
                print(d[j], sep = '', end = ' ')
        if text[i] == ' ':
            print(sep = '', end = '   ')
    print('\n')

def decode(d):
    symbol, space = '', ''
    text = input('Type your morse code:\n').upper()
    for i in range(len(text)):
        if text[i] != ' ':
            symbol += text[i]
            space = ''
            continue
        if text[i] == ' ':
            space += text[i]
            if space == '    ':
                print(' ',sep = '', end = '')
                space = ''
                continue
        for j in d.keys():
            if symbol == j:
                print(d[j], sep = '', end = '')
                symbol = ''
                space = ''
    for j in d.keys():
        if symbol == j:
            print(d[j], sep = '', end = ' ')
            symbol = ''
    print('\n')

